CREATE TABLE `hydt` (
  `id` int(12) unsigned NOT NULL AUTO_INCREMENT,
  `biaoti` varchar(255) DEFAULT NULL,
  `zhengwen` mediumtext,
  `tianjiaren` varchar(155) DEFAULT NULL,
  `tianjiashijian` varchar(155) DEFAULT NULL,
  `ywlj` varchar(355) DEFAULT NULL COMMENT '原文链接',
  `gengxinren` varchar(155) DEFAULT NULL,
  `gengxinshijian` varchar(155) DEFAULT NULL,
  `leixing` int(2) DEFAULT NULL COMMENT '1.企业新闻 2.高校新闻 3.行业动态',
  `paixu` int(12) DEFAULT NULL,
  PRIMARY KEY (`id`)
