/*Table structure for table `kjxz` */

DROP TABLE IF EXISTS `kjxz`;

CREATE TABLE `kjxz` (
  `id` int(12) NOT NULL AUTO_INCREMENT,
  `biaoti` varchar(255) NOT NULL,
  `jieshao` mediumtext,
  `dyts` varchar(255) DEFAULT NULL COMMENT '对应的图书',
  `fujian` varchar(255) DEFAULT NULL COMMENT '附件',
  `bdfx` varchar(255) DEFAULT NULL COMMENT '百度云盘分享链接',
  `fxmm` varchar(16) DEFAULT NULL COMMENT '分享密码',
  `tupian` varchar(155) DEFAULT NULL,
  `tianjiaren` varchar(155) DEFAULT NULL,
  `tianjiashijian` varchar(155) DEFAULT NULL,
  `gengxinren` varchar(155) DEFAULT NULL,
  `gengxinshijian` varchar(155) DEFAULT NULL,
