/*Table structure for table `swny` */

DROP TABLE IF EXISTS `swny`;

CREATE TABLE `swny` (
  `id` int(12) NOT NULL AUTO_INCREMENT,
  `fenlei` varchar(155) DEFAULT NULL COMMENT '高等文科下的分类',
  `fenlei2` varchar(155) DEFAULT NULL,
  `shuming` varchar(255) NOT NULL COMMENT '图书名',
  `zuozhe` varchar(255) NOT NULL COMMENT '作者',
  `chubanshe` varchar(255) NOT NULL COMMENT '出版社',
  `isbn` varchar(155) NOT NULL COMMENT 'ISBN',
  `banci` int(2) DEFAULT '1' COMMENT '版次',
  `baozhuang` varchar(155) DEFAULT NULL COMMENT '简装/平装',
  `kaiben` int(2) DEFAULT NULL COMMENT '开本',
  `chubanshijian` varchar(155) DEFAULT NULL COMMENT '出版时间',
  `yeshu` int(5) DEFAULT NULL COMMENT '页数',
  `dingjia` float DEFAULT NULL,
  `neirongjianjie` mediumtext,
  `zuozhejianjie` mediumtext,
  `mulu` mediumtext,
  `jingcaishuzhai` mediumtext,
  `qianyan` mediumtext,
  `tianjiashijian` varchar(155) DEFAULT NULL,
  `tianjiaren` varchar(155) DEFAULT NULL,
  `tupian1` varchar(155) DEFAULT NULL,
  `tupian2` varchar(155) DEFAULT NULL,
  `tupian3` varchar(155) DEFAULT NULL,
  `tupian4` varchar(155) DEFAULT NULL,
  `gengxinren` varchar(155) DEFAULT NULL,
  `gengxinshijian` varchar(155) DEFAULT NULL,
  `zhanshi` int(1) DEFAULT '0' COMMENT '1.首页展示 0.不展示',
  `zhanshishijian` timestamp NULL DEFAULT NULL COMMENT '设置展示时间',
  `paixu` int(12) DEFAULT NULL COMMENT '排序',
  `zhiding` int(1) DEFAULT '0' COMMENT '1.置顶 0.不置顶',
  `zhidingshijian` timestamp NULL DEFAULT NULL,
  `type` varchar(55) DEFAULT 'swny',
  PRIMARY KEY (`id`),
  KEY `isbn` (`isbn`),
  FULLTEXT KEY `shuming` (`shuming`),
  FULLTEXT KEY `zuozhe` (`zuozhe`)
) ENGINE=MyISAM AUTO_INCREMENT=33 DEFAULT CHARSET=utf8;

/*Data for the table `swny` */

insert  into `swny`(`id`,`fenlei`,`fenlei2`,`shuming`,`zuozhe`,`chubanshe`,`isbn`,`banci`,`baozhuang`,`kaiben`,`chubanshijian`,`yeshu`,`dingjia`,`neirongjianjie`,`zuozhejianjie`,`mulu`,`jingcaishuzhai`,`qianyan`,`tianjiashijian`,`tianjiaren`,`tupian1`,`tupian2`,`tupian3`,`tupian4`,`gengxinren`,`gengxinshijian`,`zhanshi`,`zhanshishijian`,`paixu`,`zhiding`,`zhidingshijian`,`type`) values (1,'数学类','物理类','会计学原理','袁伟','高等教育出版社','123213',1,'平装',16,'2012-12-31',678,123.2,'本教材系统地介绍了我国商业银行会计的基础理论、基本知识和方法技能，详尽地反映了商业银行基本业务的会计处理。本教材的编写立足于应用型人才培养，吸收了多所应用型本科院校一些成熟教材的精华，融入了编者在实务及教学工作中的经验和体会，着力培养学生实践能力和创新能力。本教材以教学和实际工作需要为目标，按照教育部公布的教学基本要求，以简明、精练的语言阐述知识要点，深入浅出、通俗易懂，理论联系实际，实务操作规范，符合市场经济条件下的应用型人才培养的要求。本教材除正文外，每章还设计了本章导言、本章知识结构图、 本章小结、复习思考题、延伸阅读等环节，使其在形式上更加新颖，在结构上更加完整，从而更有利于学生自主学习。<br>','本教材系统地介绍了我国商业银行会计的基础理论、基本知识和方法技能，详尽地反映了商业银行基本业务的会计处理。本教材的编写立足于应用型人才培养，吸收了多所应用型本科院校一些成熟教材的精华，融入了编者在实务及教学工作中的经验和体会，着力培养学生实践能力和创新能力。本教材以教学和实际工作需要为目标，按照教育部公布的教学基本要求，以简明、精练的语言阐述知识要点，深入浅出、通俗易懂，理论联系实际，实务操作规范，符合市场经济条件下的应用型人才培养的要求。本教材除正文外，每章还设计了本章导言、本章知识结构图、 本章小结、复习思考题、延伸阅读等环节，使其在形式上更加新颖，在结构上更加完整，从而更有利于学生自主学习。<br>',NULL,NULL,NULL,'2015-06-30 13:35:57','Admin','4732b2ca-c53d-4d2a-bdaf-51343212f5f2.jpg','1b90ce64-e848-41cc-b708-515b4ea4cd9d.jpg','92c4c440-7a7b-4c4e-a76f-b22fdff6c4ef.jpg','a3b38b62-daee-4c98-b5d9-52edfb727039.jpg',NULL,NULL,0,NULL,1,0,NULL,'swny'),(2,'数学类','物理类','国际金融','贺瑛','高等教育出版社','23423489',1,'平装',16,'2012-12-12',122,12.1,'<p>《国际金融/高等学校应用创新型人才培训系列教材》注重国际金融理论的应用性和实用性，在保证国际金融基本理论完整的前提下，侧重于国际金融实务的操作。在分析国际金融理论和知识时，注重结合中国的对外货币金融问题进行说明。同时注意吸取目前较先进的教学经验，以及本学科应用领域的最新研究成果和知识。全书共分10章，主要阐述国际金融概述、国际收支、外汇与汇率、外汇市场与外汇交易、汇率制度、国际储备、国际金融市场、国际货币体系、国际资本流动、国际金融机构等内容。《国际金融/高等学校应用创新型人才培训系列教材》采用国际流行的教材编写体例，每章前都有本章导言，章后有本章小结、延伸阅读、本章案例和复习思考题，篇中提供了与教材知识点相配套的拓展阅读内容。</p><p>本书适合普通高校作为经济管理类专业的专业基础课教材，也可作为金融、经济从业人员的培训教材，还可以作为广大社会工作者了解国际金融知识的阅读书籍。<br></p>','<p>《国际金融/高等学校应用创新型人才培训系列教材》注重国际金融理论的应用性和实用性，在保证国际金融基本理论完整的前提下，侧重于国际金融实务的操作。在分析国际金融理论和知识时，注重结合中国的对外货币金融问题进行说明。同时注意吸取目前较先进的教学经验，以及本学科应用领域的最新研究成果和知识。全书共分10章，主要阐述国际金融概述、国际收支、外汇与汇率、外汇市场与外汇交易、汇率制度、国际储备、国际金融市场、国际货币体系、国际资本流动、国际金融机构等内容。《国际金融/高等学校应用创新型人才培训系列教材》采用国际流行的教材编写体例，每章前都有本章导言，章后有本章小结、延伸阅读、本章案例和复习思考题，篇中提供了与教材知识点相配套的拓展阅读内容。</p><p>本书适合普通高校作为经济管理类专业的专业基础课教材，也可作为金融、经济从业人员的培训教材，还可以作为广大社会工作者了解国际金融知识的阅读书籍。<br></p>',NULL,NULL,NULL,'2015-06-30 14:05:09','Admin','0a1355bf-b33d-41e9-9ca3-16b59c427482.jpg','8315288c-819c-45fa-8081-37ad42b97ecf.jpg','a58cf1d5-1b5a-48f3-aefd-dbc62fa51335.jpg','48973f26-f5cf-471c-a4d1-e44717799744.jpg',NULL,NULL,0,NULL,2,0,NULL,'swny'),(3,'数学类','无','国际金融实务','黄梅波 熊爱宗','高等教育出版社','1232323989',4,'平装',16,'2014-03-15',348,123.2,'本书是高等学校国际经济与贸易专业主要课程教材，它对各种金融工具及其实务操作进行了详细的介绍。全书共四篇十五章。第一篇介绍了主要的外汇业务与金融衍生交易，包括外汇与外汇业务的基本概念和各种衍生工具及其操作（如期货和期货交易、期权与期权交易、互换与远期利率协议等）。第二篇对国际金融风险管理进行了探讨，包括商业银行的风险管理、工商企业的风险管理以及国家风险评估与管理等。第三篇探讨了国际融资业务，主要介绍了如何利用国际金融市场进行信贷融资、债券融资和股票融资。第四篇对跨国投资业务进行了介绍，包括跨国投资的资本成本和资本结构、国际资产组合投资以及跨国公司资本预算管理等。<p>本书可作为高等院校经济类专业本科或研究生的教学用书，也可以作为从事实际经济工作人员的参考用书。<br></p>','本书是高等学校国际经济与贸易专业主要课程教材，它对各种金融工具及其实务操作进行了详细的介绍。全书共四篇十五章。第一篇介绍了主要的外汇业务与金融衍生交易，包括外汇与外汇业务的基本概念和各种衍生工具及其操作（如期货和期货交易、期权与期权交易、互换与远期利率协议等）。第二篇对国际金融风险管理进行了探讨，包括商业银行的风险管理、工商企业的风险管理以及国家风险评估与管理等。第三篇探讨了国际融资业务，主要介绍了如何利用国际金融市场进行信贷融资、债券融资和股票融资。第四篇对跨国投资业务进行了介绍，包括跨国投资的资本成本和资本结构、国际资产组合投资以及跨国公司资本预算管理等。<p>本书可作为高等院校经济类专业本科或研究生的教学用书，也可以作为从事实际经济工作人员的参考用书。<br></p>',NULL,NULL,NULL,'2015-06-30 14:06:50','Admin','e49b98d3-bd71-4d7b-a13f-313726c9c9a5.jpg','24f9047c-452e-4120-bfac-2c7a6b99d098.jpg','82d0335a-f2e9-476b-950f-64b7bfad0eac.jpg','737c038a-1e5a-4e92-b2bd-aa1f6273f7da.jpg',NULL,NULL,0,NULL,3,0,NULL,'swny'),(4,'数学类','无','银行授信实务','陆明祥 杨则文','高等教育出版社','3123123123123',1,'平装',16,'2012-08--9',547,78.9,'<p>高等职业教育金融专业教学资源库项目（项目编号：2012-2-6）是教育部、财政部为深化高职教育教学改革，加强专业与课程建设，推动优质教学资源共建共享，提高人才培养质量而启动的国家级高职教育建设项目。本书是高等职业教育专业教学资源库建设项目规划教材，并已入选“十二五”职业教育国家规划教材立项选题。</p><p>本书是教育部“高等职业教育金融专业教学资源库建设项目”的建设成果之一。本书以金融企业的服务需求为主线，以金融服务礼仪的工作任务与要求为依据，强调内容的应用性和操作性，力争突出学生职业素质养成的主体性。本书共有九章：第一章，金融行业工作人员礼仪修养的基础理论；第二章，金融行业工作人员的仪表礼仪；第三章，金融行业工作人员的仪态礼仪；第四章，金融行业工作人员的语言礼仪；第五章，金融行业工作人员的日常交际礼仪；第六章，金融行业公务礼仪；第七章，金融行业岗位服务礼仪；第八章，金融行业营销礼仪；第九章，金融行业涉外服务礼仪。正文设有引例、同步案例、同步思考、同步训练、小常识、活动设计等内容，在结束时都提供了专业能力训练和专业能力考核的内容，便于学生自查和教师考评。</p><p>本书既可作为高等职业技术学院对学生进行素质教育和开放礼仪课的教材，也可作为金融行业员工了解和掌握金融职业礼仪知识、提高素质、加强修养的工具书。<br></p>','<p>高等职业教育金融专业教学资源库项目（项目编号：2012-2-6）是教育部、财政部为深化高职教育教学改革，加强专业与课程建设，推动优质教学资源共建共享，提高人才培养质量而启动的国家级高职教育建设项目。本书是高等职业教育专业教学资源库建设项目规划教材，并已入选“十二五”职业教育国家规划教材立项选题。</p><p>本书是教育部“高等职业教育金融专业教学资源库建设项目”的建设成果之一。本书以金融企业的服务需求为主线，以金融服务礼仪的工作任务与要求为依据，强调内容的应用性和操作性，力争突出学生职业素质养成的主体性。本书共有九章：第一章，金融行业工作人员礼仪修养的基础理论；第二章，金融行业工作人员的仪表礼仪；第三章，金融行业工作人员的仪态礼仪；第四章，金融行业工作人员的语言礼仪；第五章，金融行业工作人员的日常交际礼仪；第六章，金融行业公务礼仪；第七章，金融行业岗位服务礼仪；第八章，金融行业营销礼仪；第九章，金融行业涉外服务礼仪。正文设有引例、同步案例、同步思考、同步训练、小常识、活动设计等内容，在结束时都提供了专业能力训练和专业能力考核的内容，便于学生自查和教师考评。</p><p>本书既可作为高等职业技术学院对学生进行素质教育和开放礼仪课的教材，也可作为金融行业员工了解和掌握金融职业礼仪知识、提高素质、加强修养的工具书。<br></p>',NULL,NULL,NULL,'2015-06-30 14:08:58','Admin','05b4ea9e-29cc-4202-bef6-9ccfd5e2630f.jpg','8b19b1a4-486d-4cde-a153-114d52978175.jpg','0478c143-361a-4f80-9d58-b32b7273483b.jpg','a82fa9f0-9095-4b6c-a9d5-acc39e93398c.jpg',NULL,NULL,0,NULL,4,0,NULL,'swny'),(5,'数学类','物理类','会计学原理','袁伟','高等教育出版社','123213',1,'平装',16,'2012-12-31',678,123.2,'本教材系统地介绍了我国商业银行会计的基础理论、基本知识和方法技能，详尽地反映了商业银行基本业务的会计处理。本教材的编写立足于应用型人才培养，吸收了多所应用型本科院校一些成熟教材的精华，融入了编者在实务及教学工作中的经验和体会，着力培养学生实践能力和创新能力。本教材以教学和实际工作需要为目标，按照教育部公布的教学基本要求，以简明、精练的语言阐述知识要点，深入浅出、通俗易懂，理论联系实际，实务操作规范，符合市场经济条件下的应用型人才培养的要求。本教材除正文外，每章还设计了本章导言、本章知识结构图、 本章小结、复习思考题、延伸阅读等环节，使其在形式上更加新颖，在结构上更加完整，从而更有利于学生自主学习。<br>','本教材系统地介绍了我国商业银行会计的基础理论、基本知识和方法技能，详尽地反映了商业银行基本业务的会计处理。本教材的编写立足于应用型人才培养，吸收了多所应用型本科院校一些成熟教材的精华，融入了编者在实务及教学工作中的经验和体会，着力培养学生实践能力和创新能力。本教材以教学和实际工作需要为目标，按照教育部公布的教学基本要求，以简明、精练的语言阐述知识要点，深入浅出、通俗易懂，理论联系实际，实务操作规范，符合市场经济条件下的应用型人才培养的要求。本教材除正文外，每章还设计了本章导言、本章知识结构图、 本章小结、复习思考题、延伸阅读等环节，使其在形式上更加新颖，在结构上更加完整，从而更有利于学生自主学习。<br>',NULL,NULL,NULL,'2015-06-30 13:35:57','Admin','4732b2ca-c53d-4d2a-bdaf-51343212f5f2.jpg','1b90ce64-e848-41cc-b708-515b4ea4cd9d.jpg','92c4c440-7a7b-4c4e-a76f-b22fdff6c4ef.jpg','a3b38b62-daee-4c98-b5d9-52edfb727039.jpg',NULL,NULL,0,NULL,5,0,NULL,'swny'),(6,'数学类','物理类','国际金融','贺瑛','高等教育出版社','23423489',1,'平装',16,'2012-12-12',122,12.1,'<p>《国际金融/高等学校应用创新型人才培训系列教材》注重国际金融理论的应用性和实用性，在保证国际金融基本理论完整的前提下，侧重于国际金融实务的操作。在分析国际金融理论和知识时，注重结合中国的对外货币金融问题进行说明。同时注意吸取目前较先进的教学经验，以及本学科应用领域的最新研究成果和知识。全书共分10章，主要阐述国际金融概述、国际收支、外汇与汇率、外汇市场与外汇交易、汇率制度、国际储备、国际金融市场、国际货币体系、国际资本流动、国际金融机构等内容。《国际金融/高等学校应用创新型人才培训系列教材》采用国际流行的教材编写体例，每章前都有本章导言，章后有本章小结、延伸阅读、本章案例和复习思考题，篇中提供了与教材知识点相配套的拓展阅读内容。</p><p>本书适合普通高校作为经济管理类专业的专业基础课教材，也可作为金融、经济从业人员的培训教材，还可以作为广大社会工作者了解国际金融知识的阅读书籍。<br></p>','<p>《国际金融/高等学校应用创新型人才培训系列教材》注重国际金融理论的应用性和实用性，在保证国际金融基本理论完整的前提下，侧重于国际金融实务的操作。在分析国际金融理论和知识时，注重结合中国的对外货币金融问题进行说明。同时注意吸取目前较先进的教学经验，以及本学科应用领域的最新研究成果和知识。全书共分10章，主要阐述国际金融概述、国际收支、外汇与汇率、外汇市场与外汇交易、汇率制度、国际储备、国际金融市场、国际货币体系、国际资本流动、国际金融机构等内容。《国际金融/高等学校应用创新型人才培训系列教材》采用国际流行的教材编写体例，每章前都有本章导言，章后有本章小结、延伸阅读、本章案例和复习思考题，篇中提供了与教材知识点相配套的拓展阅读内容。</p><p>本书适合普通高校作为经济管理类专业的专业基础课教材，也可作为金融、经济从业人员的培训教材，还可以作为广大社会工作者了解国际金融知识的阅读书籍。<br></p>',NULL,NULL,NULL,'2015-06-30 14:05:09','Admin','0a1355bf-b33d-41e9-9ca3-16b59c427482.jpg','8315288c-819c-45fa-8081-37ad42b97ecf.jpg','a58cf1d5-1b5a-48f3-aefd-dbc62fa51335.jpg','48973f26-f5cf-471c-a4d1-e44717799744.jpg',NULL,NULL,0,NULL,6,0,NULL,'swny'),(7,'数学类','无','国际金融实务','黄梅波 熊爱宗','高等教育出版社','1232323989',4,'平装',16,'2014-03-15',348,123.2,'本书是高等学校国际经济与贸易专业主要课程教材，它对各种金融工具及其实务操作进行了详细的介绍。全书共四篇十五章。第一篇介绍了主要的外汇业务与金融衍生交易，包括外汇与外汇业务的基本概念和各种衍生工具及其操作（如期货和期货交易、期权与期权交易、互换与远期利率协议等）。第二篇对国际金融风险管理进行了探讨，包括商业银行的风险管理、工商企业的风险管理以及国家风险评估与管理等。第三篇探讨了国际融资业务，主要介绍了如何利用国际金融市场进行信贷融资、债券融资和股票融资。第四篇对跨国投资业务进行了介绍，包括跨国投资的资本成本和资本结构、国际资产组合投资以及跨国公司资本预算管理等。<p>本书可作为高等院校经济类专业本科或研究生的教学用书，也可以作为从事实际经济工作人员的参考用书。<br></p>','本书是高等学校国际经济与贸易专业主要课程教材，它对各种金融工具及其实务操作进行了详细的介绍。全书共四篇十五章。第一篇介绍了主要的外汇业务与金融衍生交易，包括外汇与外汇业务的基本概念和各种衍生工具及其操作（如期货和期货交易、期权与期权交易、互换与远期利率协议等）。第二篇对国际金融风险管理进行了探讨，包括商业银行的风险管理、工商企业的风险管理以及国家风险评估与管理等。第三篇探讨了国际融资业务，主要介绍了如何利用国际金融市场进行信贷融资、债券融资和股票融资。第四篇对跨国投资业务进行了介绍，包括跨国投资的资本成本和资本结构、国际资产组合投资以及跨国公司资本预算管理等。<p>本书可作为高等院校经济类专业本科或研究生的教学用书，也可以作为从事实际经济工作人员的参考用书。<br></p>',NULL,NULL,NULL,'2015-06-30 14:06:50','Admin','e49b98d3-bd71-4d7b-a13f-313726c9c9a5.jpg','24f9047c-452e-4120-bfac-2c7a6b99d098.jpg','82d0335a-f2e9-476b-950f-64b7bfad0eac.jpg','737c038a-1e5a-4e92-b2bd-aa1f6273f7da.jpg',NULL,NULL,0,NULL,7,0,NULL,'swny'),(8,'数学类','无','银行授信实务','陆明祥 杨则文','高等教育出版社','3123123123123',1,'平装',16,'2012-08--9',547,78.9,'<p>高等职业教育金融专业教学资源库项目（项目编号：2012-2-6）是教育部、财政部为深化高职教育教学改革，加强专业与课程建设，推动优质教学资源共建共享，提高人才培养质量而启动的国家级高职教育建设项目。本书是高等职业教育专业教学资源库建设项目规划教材，并已入选“十二五”职业教育国家规划教材立项选题。</p><p>本书是教育部“高等职业教育金融专业教学资源库建设项目”的建设成果之一。本书以金融企业的服务需求为主线，以金融服务礼仪的工作任务与要求为依据，强调内容的应用性和操作性，力争突出学生职业素质养成的主体性。本书共有九章：第一章，金融行业工作人员礼仪修养的基础理论；第二章，金融行业工作人员的仪表礼仪；第三章，金融行业工作人员的仪态礼仪；第四章，金融行业工作人员的语言礼仪；第五章，金融行业工作人员的日常交际礼仪；第六章，金融行业公务礼仪；第七章，金融行业岗位服务礼仪；第八章，金融行业营销礼仪；第九章，金融行业涉外服务礼仪。正文设有引例、同步案例、同步思考、同步训练、小常识、活动设计等内容，在结束时都提供了专业能力训练和专业能力考核的内容，便于学生自查和教师考评。</p><p>本书既可作为高等职业技术学院对学生进行素质教育和开放礼仪课的教材，也可作为金融行业员工了解和掌握金融职业礼仪知识、提高素质、加强修养的工具书。<br></p>','<p>高等职业教育金融专业教学资源库项目（项目编号：2012-2-6）是教育部、财政部为深化高职教育教学改革，加强专业与课程建设，推动优质教学资源共建共享，提高人才培养质量而启动的国家级高职教育建设项目。本书是高等职业教育专业教学资源库建设项目规划教材，并已入选“十二五”职业教育国家规划教材立项选题。</p><p>本书是教育部“高等职业教育金融专业教学资源库建设项目”的建设成果之一。本书以金融企业的服务需求为主线，以金融服务礼仪的工作任务与要求为依据，强调内容的应用性和操作性，力争突出学生职业素质养成的主体性。本书共有九章：第一章，金融行业工作人员礼仪修养的基础理论；第二章，金融行业工作人员的仪表礼仪；第三章，金融行业工作人员的仪态礼仪；第四章，金融行业工作人员的语言礼仪；第五章，金融行业工作人员的日常交际礼仪；第六章，金融行业公务礼仪；第七章，金融行业岗位服务礼仪；第八章，金融行业营销礼仪；第九章，金融行业涉外服务礼仪。正文设有引例、同步案例、同步思考、同步训练、小常识、活动设计等内容，在结束时都提供了专业能力训练和专业能力考核的内容，便于学生自查和教师考评。</p><p>本书既可作为高等职业技术学院对学生进行素质教育和开放礼仪课的教材，也可作为金融行业员工了解和掌握金融职业礼仪知识、提高素质、加强修养的工具书。<br></p>',NULL,NULL,NULL,'2015-06-30 14:08:58','Admin','05b4ea9e-29cc-4202-bef6-9ccfd5e2630f.jpg','8b19b1a4-486d-4cde-a153-114d52978175.jpg','0478c143-361a-4f80-9d58-b32b7273483b.jpg','a82fa9f0-9095-4b6c-a9d5-acc39e93398c.jpg',NULL,NULL,0,NULL,8,0,NULL,'swny'),(9,'数学类','物理类','会计学原理','袁伟','高等教育出版社','123213',1,'平装',16,'2012-12-31',678,123.2,'本教材系统地介绍了我国商业银行会计的基础理论、基本知识和方法技能，详尽地反映了商业银行基本业务的会计处理。本教材的编写立足于应用型人才培养，吸收了多所应用型本科院校一些成熟教材的精华，融入了编者在实务及教学工作中的经验和体会，着力培养学生实践能力和创新能力。本教材以教学和实际工作需要为目标，按照教育部公布的教学基本要求，以简明、精练的语言阐述知识要点，深入浅出、通俗易懂，理论联系实际，实务操作规范，符合市场经济条件下的应用型人才培养的要求。本教材除正文外，每章还设计了本章导言、本章知识结构图、 本章小结、复习思考题、延伸阅读等环节，使其在形式上更加新颖，在结构上更加完整，从而更有利于学生自主学习。<br>','本教材系统地介绍了我国商业银行会计的基础理论、基本知识和方法技能，详尽地反映了商业银行基本业务的会计处理。本教材的编写立足于应用型人才培养，吸收了多所应用型本科院校一些成熟教材的精华，融入了编者在实务及教学工作中的经验和体会，着力培养学生实践能力和创新能力。本教材以教学和实际工作需要为目标，按照教育部公布的教学基本要求，以简明、精练的语言阐述知识要点，深入浅出、通俗易懂，理论联系实际，实务操作规范，符合市场经济条件下的应用型人才培养的要求。本教材除正文外，每章还设计了本章导言、本章知识结构图、 本章小结、复习思考题、延伸阅读等环节，使其在形式上更加新颖，在结构上更加完整，从而更有利于学生自主学习。<br>',NULL,NULL,NULL,'2015-06-30 13:35:57','Admin','4732b2ca-c53d-4d2a-bdaf-51343212f5f2.jpg','1b90ce64-e848-41cc-b708-515b4ea4cd9d.jpg','92c4c440-7a7b-4c4e-a76f-b22fdff6c4ef.jpg','a3b38b62-daee-4c98-b5d9-52edfb727039.jpg',NULL,NULL,0,NULL,9,0,NULL,'swny'),(10,'数学类','物理类','国际金融','贺瑛','高等教育出版社','23423489',1,'平装',16,'2012-12-12',122,12.1,'<p>《国际金融/高等学校应用创新型人才培训系列教材》注重国际金融理论的应用性和实用性，在保证国际金融基本理论完整的前提下，侧重于国际金融实务的操作。在分析国际金融理论和知识时，注重结合中国的对外货币金融问题进行说明。同时注意吸取目前较先进的教学经验，以及本学科应用领域的最新研究成果和知识。全书共分10章，主要阐述国际金融概述、国际收支、外汇与汇率、外汇市场与外汇交易、汇率制度、国际储备、国际金融市场、国际货币体系、国际资本流动、国际金融机构等内容。《国际金融/高等学校应用创新型人才培训系列教材》采用国际流行的教材编写体例，每章前都有本章导言，章后有本章小结、延伸阅读、本章案例和复习思考题，篇中提供了与教材知识点相配套的拓展阅读内容。</p><p>本书适合普通高校作为经济管理类专业的专业基础课教材，也可作为金融、经济从业人员的培训教材，还可以作为广大社会工作者了解国际金融知识的阅读书籍。<br></p>','<p>《国际金融/高等学校应用创新型人才培训系列教材》注重国际金融理论的应用性和实用性，在保证国际金融基本理论完整的前提下，侧重于国际金融实务的操作。在分析国际金融理论和知识时，注重结合中国的对外货币金融问题进行说明。同时注意吸取目前较先进的教学经验，以及本学科应用领域的最新研究成果和知识。全书共分10章，主要阐述国际金融概述、国际收支、外汇与汇率、外汇市场与外汇交易、汇率制度、国际储备、国际金融市场、国际货币体系、国际资本流动、国际金融机构等内容。《国际金融/高等学校应用创新型人才培训系列教材》采用国际流行的教材编写体例，每章前都有本章导言，章后有本章小结、延伸阅读、本章案例和复习思考题，篇中提供了与教材知识点相配套的拓展阅读内容。</p><p>本书适合普通高校作为经济管理类专业的专业基础课教材，也可作为金融、经济从业人员的培训教材，还可以作为广大社会工作者了解国际金融知识的阅读书籍。<br></p>',NULL,NULL,NULL,'2015-06-30 14:05:09','Admin','0a1355bf-b33d-41e9-9ca3-16b59c427482.jpg','8315288c-819c-45fa-8081-37ad42b97ecf.jpg','a58cf1d5-1b5a-48f3-aefd-dbc62fa51335.jpg','48973f26-f5cf-471c-a4d1-e44717799744.jpg',NULL,NULL,0,NULL,10,0,NULL,'swny'),(11,'数学类','无','国际金融实务','黄梅波 熊爱宗','高等教育出版社','1232323989',4,'平装',16,'2014-03-15',348,123.2,'本书是高等学校国际经济与贸易专业主要课程教材，它对各种金融工具及其实务操作进行了详细的介绍。全书共四篇十五章。第一篇介绍了主要的外汇业务与金融衍生交易，包括外汇与外汇业务的基本概念和各种衍生工具及其操作（如期货和期货交易、期权与期权交易、互换与远期利率协议等）。第二篇对国际金融风险管理进行了探讨，包括商业银行的风险管理、工商企业的风险管理以及国家风险评估与管理等。第三篇探讨了国际融资业务，主要介绍了如何利用国际金融市场进行信贷融资、债券融资和股票融资。第四篇对跨国投资业务进行了介绍，包括跨国投资的资本成本和资本结构、国际资产组合投资以及跨国公司资本预算管理等。<p>本书可作为高等院校经济类专业本科或研究生的教学用书，也可以作为从事实际经济工作人员的参考用书。<br></p>','本书是高等学校国际经济与贸易专业主要课程教材，它对各种金融工具及其实务操作进行了详细的介绍。全书共四篇十五章。第一篇介绍了主要的外汇业务与金融衍生交易，包括外汇与外汇业务的基本概念和各种衍生工具及其操作（如期货和期货交易、期权与期权交易、互换与远期利率协议等）。第二篇对国际金融风险管理进行了探讨，包括商业银行的风险管理、工商企业的风险管理以及国家风险评估与管理等。第三篇探讨了国际融资业务，主要介绍了如何利用国际金融市场进行信贷融资、债券融资和股票融资。第四篇对跨国投资业务进行了介绍，包括跨国投资的资本成本和资本结构、国际资产组合投资以及跨国公司资本预算管理等。<p>本书可作为高等院校经济类专业本科或研究生的教学用书，也可以作为从事实际经济工作人员的参考用书。<br></p>',NULL,NULL,NULL,'2015-06-30 14:06:50','Admin','e49b98d3-bd71-4d7b-a13f-313726c9c9a5.jpg','24f9047c-452e-4120-bfac-2c7a6b99d098.jpg','82d0335a-f2e9-476b-950f-64b7bfad0eac.jpg','737c038a-1e5a-4e92-b2bd-aa1f6273f7da.jpg',NULL,NULL,0,NULL,11,0,NULL,'swny'),(12,'数学类','无','银行授信实务','陆明祥 杨则文','高等教育出版社','3123123123123',1,'平装',16,'2012-08--9',547,78.9,'<p>高等职业教育金融专业教学资源库项目（项目编号：2012-2-6）是教育部、财政部为深化高职教育教学改革，加强专业与课程建设，推动优质教学资源共建共享，提高人才培养质量而启动的国家级高职教育建设项目。本书是高等职业教育专业教学资源库建设项目规划教材，并已入选“十二五”职业教育国家规划教材立项选题。</p><p>本书是教育部“高等职业教育金融专业教学资源库建设项目”的建设成果之一。本书以金融企业的服务需求为主线，以金融服务礼仪的工作任务与要求为依据，强调内容的应用性和操作性，力争突出学生职业素质养成的主体性。本书共有九章：第一章，金融行业工作人员礼仪修养的基础理论；第二章，金融行业工作人员的仪表礼仪；第三章，金融行业工作人员的仪态礼仪；第四章，金融行业工作人员的语言礼仪；第五章，金融行业工作人员的日常交际礼仪；第六章，金融行业公务礼仪；第七章，金融行业岗位服务礼仪；第八章，金融行业营销礼仪；第九章，金融行业涉外服务礼仪。正文设有引例、同步案例、同步思考、同步训练、小常识、活动设计等内容，在结束时都提供了专业能力训练和专业能力考核的内容，便于学生自查和教师考评。</p><p>本书既可作为高等职业技术学院对学生进行素质教育和开放礼仪课的教材，也可作为金融行业员工了解和掌握金融职业礼仪知识、提高素质、加强修养的工具书。<br></p>','<p>高等职业教育金融专业教学资源库项目（项目编号：2012-2-6）是教育部、财政部为深化高职教育教学改革，加强专业与课程建设，推动优质教学资源共建共享，提高人才培养质量而启动的国家级高职教育建设项目。本书是高等职业教育专业教学资源库建设项目规划教材，并已入选“十二五”职业教育国家规划教材立项选题。</p><p>本书是教育部“高等职业教育金融专业教学资源库建设项目”的建设成果之一。本书以金融企业的服务需求为主线，以金融服务礼仪的工作任务与要求为依据，强调内容的应用性和操作性，力争突出学生职业素质养成的主体性。本书共有九章：第一章，金融行业工作人员礼仪修养的基础理论；第二章，金融行业工作人员的仪表礼仪；第三章，金融行业工作人员的仪态礼仪；第四章，金融行业工作人员的语言礼仪；第五章，金融行业工作人员的日常交际礼仪；第六章，金融行业公务礼仪；第七章，金融行业岗位服务礼仪；第八章，金融行业营销礼仪；第九章，金融行业涉外服务礼仪。正文设有引例、同步案例、同步思考、同步训练、小常识、活动设计等内容，在结束时都提供了专业能力训练和专业能力考核的内容，便于学生自查和教师考评。</p><p>本书既可作为高等职业技术学院对学生进行素质教育和开放礼仪课的教材，也可作为金融行业员工了解和掌握金融职业礼仪知识、提高素质、加强修养的工具书。<br></p>',NULL,NULL,NULL,'2015-06-30 14:08:58','Admin','05b4ea9e-29cc-4202-bef6-9ccfd5e2630f.jpg','8b19b1a4-486d-4cde-a153-114d52978175.jpg','0478c143-361a-4f80-9d58-b32b7273483b.jpg','a82fa9f0-9095-4b6c-a9d5-acc39e93398c.jpg',NULL,NULL,0,NULL,12,0,NULL,'swny'),(13,'数学类','物理类','会计学原理','袁伟','高等教育出版社','123213',1,'平装',16,'2012-12-31',678,123.2,'本教材系统地介绍了我国商业银行会计的基础理论、基本知识和方法技能，详尽地反映了商业银行基本业务的会计处理。本教材的编写立足于应用型人才培养，吸收了多所应用型本科院校一些成熟教材的精华，融入了编者在实务及教学工作中的经验和体会，着力培养学生实践能力和创新能力。本教材以教学和实际工作需要为目标，按照教育部公布的教学基本要求，以简明、精练的语言阐述知识要点，深入浅出、通俗易懂，理论联系实际，实务操作规范，符合市场经济条件下的应用型人才培养的要求。本教材除正文外，每章还设计了本章导言、本章知识结构图、 本章小结、复习思考题、延伸阅读等环节，使其在形式上更加新颖，在结构上更加完整，从而更有利于学生自主学习。<br>','本教材系统地介绍了我国商业银行会计的基础理论、基本知识和方法技能，详尽地反映了商业银行基本业务的会计处理。本教材的编写立足于应用型人才培养，吸收了多所应用型本科院校一些成熟教材的精华，融入了编者在实务及教学工作中的经验和体会，着力培养学生实践能力和创新能力。本教材以教学和实际工作需要为目标，按照教育部公布的教学基本要求，以简明、精练的语言阐述知识要点，深入浅出、通俗易懂，理论联系实际，实务操作规范，符合市场经济条件下的应用型人才培养的要求。本教材除正文外，每章还设计了本章导言、本章知识结构图、 本章小结、复习思考题、延伸阅读等环节，使其在形式上更加新颖，在结构上更加完整，从而更有利于学生自主学习。<br>',NULL,NULL,NULL,'2015-06-30 13:35:57','Admin','4732b2ca-c53d-4d2a-bdaf-51343212f5f2.jpg','1b90ce64-e848-41cc-b708-515b4ea4cd9d.jpg','92c4c440-7a7b-4c4e-a76f-b22fdff6c4ef.jpg','a3b38b62-daee-4c98-b5d9-52edfb727039.jpg',NULL,NULL,0,NULL,13,0,NULL,'swny'),(14,'数学类','物理类','国际金融','贺瑛','高等教育出版社','23423489',1,'平装',16,'2012-12-12',122,12.1,'<p>《国际金融/高等学校应用创新型人才培训系列教材》注重国际金融理论的应用性和实用性，在保证国际金融基本理论完整的前提下，侧重于国际金融实务的操作。在分析国际金融理论和知识时，注重结合中国的对外货币金融问题进行说明。同时注意吸取目前较先进的教学经验，以及本学科应用领域的最新研究成果和知识。全书共分10章，主要阐述国际金融概述、国际收支、外汇与汇率、外汇市场与外汇交易、汇率制度、国际储备、国际金融市场、国际货币体系、国际资本流动、国际金融机构等内容。《国际金融/高等学校应用创新型人才培训系列教材》采用国际流行的教材编写体例，每章前都有本章导言，章后有本章小结、延伸阅读、本章案例和复习思考题，篇中提供了与教材知识点相配套的拓展阅读内容。</p><p>本书适合普通高校作为经济管理类专业的专业基础课教材，也可作为金融、经济从业人员的培训教材，还可以作为广大社会工作者了解国际金融知识的阅读书籍。<br></p>','<p>《国际金融/高等学校应用创新型人才培训系列教材》注重国际金融理论的应用性和实用性，在保证国际金融基本理论完整的前提下，侧重于国际金融实务的操作。在分析国际金融理论和知识时，注重结合中国的对外货币金融问题进行说明。同时注意吸取目前较先进的教学经验，以及本学科应用领域的最新研究成果和知识。全书共分10章，主要阐述国际金融概述、国际收支、外汇与汇率、外汇市场与外汇交易、汇率制度、国际储备、国际金融市场、国际货币体系、国际资本流动、国际金融机构等内容。《国际金融/高等学校应用创新型人才培训系列教材》采用国际流行的教材编写体例，每章前都有本章导言，章后有本章小结、延伸阅读、本章案例和复习思考题，篇中提供了与教材知识点相配套的拓展阅读内容。</p><p>本书适合普通高校作为经济管理类专业的专业基础课教材，也可作为金融、经济从业人员的培训教材，还可以作为广大社会工作者了解国际金融知识的阅读书籍。<br></p>',NULL,NULL,NULL,'2015-06-30 14:05:09','Admin','0a1355bf-b33d-41e9-9ca3-16b59c427482.jpg','8315288c-819c-45fa-8081-37ad42b97ecf.jpg','a58cf1d5-1b5a-48f3-aefd-dbc62fa51335.jpg','48973f26-f5cf-471c-a4d1-e44717799744.jpg',NULL,NULL,0,NULL,14,0,NULL,'swny'),(15,'数学类','无','国际金融实务','黄梅波 熊爱宗','高等教育出版社','1232323989',4,'平装',16,'2014-03-15',348,123.2,'本书是高等学校国际经济与贸易专业主要课程教材，它对各种金融工具及其实务操作进行了详细的介绍。全书共四篇十五章。第一篇介绍了主要的外汇业务与金融衍生交易，包括外汇与外汇业务的基本概念和各种衍生工具及其操作（如期货和期货交易、期权与期权交易、互换与远期利率协议等）。第二篇对国际金融风险管理进行了探讨，包括商业银行的风险管理、工商企业的风险管理以及国家风险评估与管理等。第三篇探讨了国际融资业务，主要介绍了如何利用国际金融市场进行信贷融资、债券融资和股票融资。第四篇对跨国投资业务进行了介绍，包括跨国投资的资本成本和资本结构、国际资产组合投资以及跨国公司资本预算管理等。<p>本书可作为高等院校经济类专业本科或研究生的教学用书，也可以作为从事实际经济工作人员的参考用书。<br></p>','本书是高等学校国际经济与贸易专业主要课程教材，它对各种金融工具及其实务操作进行了详细的介绍。全书共四篇十五章。第一篇介绍了主要的外汇业务与金融衍生交易，包括外汇与外汇业务的基本概念和各种衍生工具及其操作（如期货和期货交易、期权与期权交易、互换与远期利率协议等）。第二篇对国际金融风险管理进行了探讨，包括商业银行的风险管理、工商企业的风险管理以及国家风险评估与管理等。第三篇探讨了国际融资业务，主要介绍了如何利用国际金融市场进行信贷融资、债券融资和股票融资。第四篇对跨国投资业务进行了介绍，包括跨国投资的资本成本和资本结构、国际资产组合投资以及跨国公司资本预算管理等。<p>本书可作为高等院校经济类专业本科或研究生的教学用书，也可以作为从事实际经济工作人员的参考用书。<br></p>',NULL,NULL,NULL,'2015-06-30 14:06:50','Admin','e49b98d3-bd71-4d7b-a13f-313726c9c9a5.jpg','24f9047c-452e-4120-bfac-2c7a6b99d098.jpg','82d0335a-f2e9-476b-950f-64b7bfad0eac.jpg','737c038a-1e5a-4e92-b2bd-aa1f6273f7da.jpg',NULL,NULL,0,NULL,15,0,NULL,'swny'),(16,'数学类','无','银行授信实务','陆明祥 杨则文','高等教育出版社','3123123123123',1,'平装',16,'2012-08--9',547,78.9,'<p>高等职业教育金融专业教学资源库项目（项目编号：2012-2-6）是教育部、财政部为深化高职教育教学改革，加强专业与课程建设，推动优质教学资源共建共享，提高人才培养质量而启动的国家级高职教育建设项目。本书是高等职业教育专业教学资源库建设项目规划教材，并已入选“十二五”职业教育国家规划教材立项选题。</p><p>本书是教育部“高等职业教育金融专业教学资源库建设项目”的建设成果之一。本书以金融企业的服务需求为主线，以金融服务礼仪的工作任务与要求为依据，强调内容的应用性和操作性，力争突出学生职业素质养成的主体性。本书共有九章：第一章，金融行业工作人员礼仪修养的基础理论；第二章，金融行业工作人员的仪表礼仪；第三章，金融行业工作人员的仪态礼仪；第四章，金融行业工作人员的语言礼仪；第五章，金融行业工作人员的日常交际礼仪；第六章，金融行业公务礼仪；第七章，金融行业岗位服务礼仪；第八章，金融行业营销礼仪；第九章，金融行业涉外服务礼仪。正文设有引例、同步案例、同步思考、同步训练、小常识、活动设计等内容，在结束时都提供了专业能力训练和专业能力考核的内容，便于学生自查和教师考评。</p><p>本书既可作为高等职业技术学院对学生进行素质教育和开放礼仪课的教材，也可作为金融行业员工了解和掌握金融职业礼仪知识、提高素质、加强修养的工具书。<br></p>','<p>高等职业教育金融专业教学资源库项目（项目编号：2012-2-6）是教育部、财政部为深化高职教育教学改革，加强专业与课程建设，推动优质教学资源共建共享，提高人才培养质量而启动的国家级高职教育建设项目。本书是高等职业教育专业教学资源库建设项目规划教材，并已入选“十二五”职业教育国家规划教材立项选题。</p><p>本书是教育部“高等职业教育金融专业教学资源库建设项目”的建设成果之一。本书以金融企业的服务需求为主线，以金融服务礼仪的工作任务与要求为依据，强调内容的应用性和操作性，力争突出学生职业素质养成的主体性。本书共有九章：第一章，金融行业工作人员礼仪修养的基础理论；第二章，金融行业工作人员的仪表礼仪；第三章，金融行业工作人员的仪态礼仪；第四章，金融行业工作人员的语言礼仪；第五章，金融行业工作人员的日常交际礼仪；第六章，金融行业公务礼仪；第七章，金融行业岗位服务礼仪；第八章，金融行业营销礼仪；第九章，金融行业涉外服务礼仪。正文设有引例、同步案例、同步思考、同步训练、小常识、活动设计等内容，在结束时都提供了专业能力训练和专业能力考核的内容，便于学生自查和教师考评。</p><p>本书既可作为高等职业技术学院对学生进行素质教育和开放礼仪课的教材，也可作为金融行业员工了解和掌握金融职业礼仪知识、提高素质、加强修养的工具书。<br></p>',NULL,NULL,NULL,'2015-06-30 14:08:58','Admin','05b4ea9e-29cc-4202-bef6-9ccfd5e2630f.jpg','8b19b1a4-486d-4cde-a153-114d52978175.jpg','0478c143-361a-4f80-9d58-b32b7273483b.jpg','a82fa9f0-9095-4b6c-a9d5-acc39e93398c.jpg',NULL,NULL,0,NULL,16,0,NULL,'swny'),(17,'数学类','物理类','会计学原理','袁伟','高等教育出版社','123213',1,'平装',16,'2012-12-31',678,123.2,'本教材系统地介绍了我国商业银行会计的基础理论、基本知识和方法技能，详尽地反映了商业银行基本业务的会计处理。本教材的编写立足于应用型人才培养，吸收了多所应用型本科院校一些成熟教材的精华，融入了编者在实务及教学工作中的经验和体会，着力培养学生实践能力和创新能力。本教材以教学和实际工作需要为目标，按照教育部公布的教学基本要求，以简明、精练的语言阐述知识要点，深入浅出、通俗易懂，理论联系实际，实务操作规范，符合市场经济条件下的应用型人才培养的要求。本教材除正文外，每章还设计了本章导言、本章知识结构图、 本章小结、复习思考题、延伸阅读等环节，使其在形式上更加新颖，在结构上更加完整，从而更有利于学生自主学习。<br>','本教材系统地介绍了我国商业银行会计的基础理论、基本知识和方法技能，详尽地反映了商业银行基本业务的会计处理。本教材的编写立足于应用型人才培养，吸收了多所应用型本科院校一些成熟教材的精华，融入了编者在实务及教学工作中的经验和体会，着力培养学生实践能力和创新能力。本教材以教学和实际工作需要为目标，按照教育部公布的教学基本要求，以简明、精练的语言阐述知识要点，深入浅出、通俗易懂，理论联系实际，实务操作规范，符合市场经济条件下的应用型人才培养的要求。本教材除正文外，每章还设计了本章导言、本章知识结构图、 本章小结、复习思考题、延伸阅读等环节，使其在形式上更加新颖，在结构上更加完整，从而更有利于学生自主学习。<br>',NULL,NULL,NULL,'2015-06-30 13:35:57','Admin','4732b2ca-c53d-4d2a-bdaf-51343212f5f2.jpg','1b90ce64-e848-41cc-b708-515b4ea4cd9d.jpg','92c4c440-7a7b-4c4e-a76f-b22fdff6c4ef.jpg','a3b38b62-daee-4c98-b5d9-52edfb727039.jpg',NULL,NULL,0,NULL,17,0,NULL,'swny'),(18,'数学类','物理类','国际金融','贺瑛','高等教育出版社','23423489',1,'平装',16,'2012-12-12',122,12.1,'<p>《国际金融/高等学校应用创新型人才培训系列教材》注重国际金融理论的应用性和实用性，在保证国际金融基本理论完整的前提下，侧重于国际金融实务的操作。在分析国际金融理论和知识时，注重结合中国的对外货币金融问题进行说明。同时注意吸取目前较先进的教学经验，以及本学科应用领域的最新研究成果和知识。全书共分10章，主要阐述国际金融概述、国际收支、外汇与汇率、外汇市场与外汇交易、汇率制度、国际储备、国际金融市场、国际货币体系、国际资本流动、国际金融机构等内容。《国际金融/高等学校应用创新型人才培训系列教材》采用国际流行的教材编写体例，每章前都有本章导言，章后有本章小结、延伸阅读、本章案例和复习思考题，篇中提供了与教材知识点相配套的拓展阅读内容。</p><p>本书适合普通高校作为经济管理类专业的专业基础课教材，也可作为金融、经济从业人员的培训教材，还可以作为广大社会工作者了解国际金融知识的阅读书籍。<br></p>','<p>《国际金融/高等学校应用创新型人才培训系列教材》注重国际金融理论的应用性和实用性，在保证国际金融基本理论完整的前提下，侧重于国际金融实务的操作。在分析国际金融理论和知识时，注重结合中国的对外货币金融问题进行说明。同时注意吸取目前较先进的教学经验，以及本学科应用领域的最新研究成果和知识。全书共分10章，主要阐述国际金融概述、国际收支、外汇与汇率、外汇市场与外汇交易、汇率制度、国际储备、国际金融市场、国际货币体系、国际资本流动、国际金融机构等内容。《国际金融/高等学校应用创新型人才培训系列教材》采用国际流行的教材编写体例，每章前都有本章导言，章后有本章小结、延伸阅读、本章案例和复习思考题，篇中提供了与教材知识点相配套的拓展阅读内容。</p><p>本书适合普通高校作为经济管理类专业的专业基础课教材，也可作为金融、经济从业人员的培训教材，还可以作为广大社会工作者了解国际金融知识的阅读书籍。<br></p>',NULL,NULL,NULL,'2015-06-30 14:05:09','Admin','0a1355bf-b33d-41e9-9ca3-16b59c427482.jpg','8315288c-819c-45fa-8081-37ad42b97ecf.jpg','a58cf1d5-1b5a-48f3-aefd-dbc62fa51335.jpg','48973f26-f5cf-471c-a4d1-e44717799744.jpg',NULL,NULL,0,NULL,18,0,NULL,'swny'),(19,'数学类','无','国际金融实务','黄梅波 熊爱宗','高等教育出版社','1232323989',4,'平装',16,'2014-03-15',348,123.2,'本书是高等学校国际经济与贸易专业主要课程教材，它对各种金融工具及其实务操作进行了详细的介绍。全书共四篇十五章。第一篇介绍了主要的外汇业务与金融衍生交易，包括外汇与外汇业务的基本概念和各种衍生工具及其操作（如期货和期货交易、期权与期权交易、互换与远期利率协议等）。第二篇对国际金融风险管理进行了探讨，包括商业银行的风险管理、工商企业的风险管理以及国家风险评估与管理等。第三篇探讨了国际融资业务，主要介绍了如何利用国际金融市场进行信贷融资、债券融资和股票融资。第四篇对跨国投资业务进行了介绍，包括跨国投资的资本成本和资本结构、国际资产组合投资以及跨国公司资本预算管理等。<p>本书可作为高等院校经济类专业本科或研究生的教学用书，也可以作为从事实际经济工作人员的参考用书。<br></p>','本书是高等学校国际经济与贸易专业主要课程教材，它对各种金融工具及其实务操作进行了详细的介绍。全书共四篇十五章。第一篇介绍了主要的外汇业务与金融衍生交易，包括外汇与外汇业务的基本概念和各种衍生工具及其操作（如期货和期货交易、期权与期权交易、互换与远期利率协议等）。第二篇对国际金融风险管理进行了探讨，包括商业银行的风险管理、工商企业的风险管理以及国家风险评估与管理等。第三篇探讨了国际融资业务，主要介绍了如何利用国际金融市场进行信贷融资、债券融资和股票融资。第四篇对跨国投资业务进行了介绍，包括跨国投资的资本成本和资本结构、国际资产组合投资以及跨国公司资本预算管理等。<p>本书可作为高等院校经济类专业本科或研究生的教学用书，也可以作为从事实际经济工作人员的参考用书。<br></p>',NULL,NULL,NULL,'2015-06-30 14:06:50','Admin','e49b98d3-bd71-4d7b-a13f-313726c9c9a5.jpg','24f9047c-452e-4120-bfac-2c7a6b99d098.jpg','82d0335a-f2e9-476b-950f-64b7bfad0eac.jpg','737c038a-1e5a-4e92-b2bd-aa1f6273f7da.jpg',NULL,NULL,0,NULL,19,0,NULL,'swny'),(20,'数学类','无','银行授信实务','陆明祥 杨则文','高等教育出版社','3123123123123',1,'平装',16,'2012-08--9',547,78.9,'<p>高等职业教育金融专业教学资源库项目（项目编号：2012-2-6）是教育部、财政部为深化高职教育教学改革，加强专业与课程建设，推动优质教学资源共建共享，提高人才培养质量而启动的国家级高职教育建设项目。本书是高等职业教育专业教学资源库建设项目规划教材，并已入选“十二五”职业教育国家规划教材立项选题。</p><p>本书是教育部“高等职业教育金融专业教学资源库建设项目”的建设成果之一。本书以金融企业的服务需求为主线，以金融服务礼仪的工作任务与要求为依据，强调内容的应用性和操作性，力争突出学生职业素质养成的主体性。本书共有九章：第一章，金融行业工作人员礼仪修养的基础理论；第二章，金融行业工作人员的仪表礼仪；第三章，金融行业工作人员的仪态礼仪；第四章，金融行业工作人员的语言礼仪；第五章，金融行业工作人员的日常交际礼仪；第六章，金融行业公务礼仪；第七章，金融行业岗位服务礼仪；第八章，金融行业营销礼仪；第九章，金融行业涉外服务礼仪。正文设有引例、同步案例、同步思考、同步训练、小常识、活动设计等内容，在结束时都提供了专业能力训练和专业能力考核的内容，便于学生自查和教师考评。</p><p>本书既可作为高等职业技术学院对学生进行素质教育和开放礼仪课的教材，也可作为金融行业员工了解和掌握金融职业礼仪知识、提高素质、加强修养的工具书。<br></p>','<p>高等职业教育金融专业教学资源库项目（项目编号：2012-2-6）是教育部、财政部为深化高职教育教学改革，加强专业与课程建设，推动优质教学资源共建共享，提高人才培养质量而启动的国家级高职教育建设项目。本书是高等职业教育专业教学资源库建设项目规划教材，并已入选“十二五”职业教育国家规划教材立项选题。</p><p>本书是教育部“高等职业教育金融专业教学资源库建设项目”的建设成果之一。本书以金融企业的服务需求为主线，以金融服务礼仪的工作任务与要求为依据，强调内容的应用性和操作性，力争突出学生职业素质养成的主体性。本书共有九章：第一章，金融行业工作人员礼仪修养的基础理论；第二章，金融行业工作人员的仪表礼仪；第三章，金融行业工作人员的仪态礼仪；第四章，金融行业工作人员的语言礼仪；第五章，金融行业工作人员的日常交际礼仪；第六章，金融行业公务礼仪；第七章，金融行业岗位服务礼仪；第八章，金融行业营销礼仪；第九章，金融行业涉外服务礼仪。正文设有引例、同步案例、同步思考、同步训练、小常识、活动设计等内容，在结束时都提供了专业能力训练和专业能力考核的内容，便于学生自查和教师考评。</p><p>本书既可作为高等职业技术学院对学生进行素质教育和开放礼仪课的教材，也可作为金融行业员工了解和掌握金融职业礼仪知识、提高素质、加强修养的工具书。<br></p>',NULL,NULL,NULL,'2015-06-30 14:08:58','Admin','05b4ea9e-29cc-4202-bef6-9ccfd5e2630f.jpg','8b19b1a4-486d-4cde-a153-114d52978175.jpg','0478c143-361a-4f80-9d58-b32b7273483b.jpg','a82fa9f0-9095-4b6c-a9d5-acc39e93398c.jpg',NULL,NULL,0,NULL,20,0,NULL,'swny'),(21,'数学类','物理类','会计学原理','袁伟','高等教育出版社','123213',1,'平装',16,'2012-12-31',678,123.2,'本教材系统地介绍了我国商业银行会计的基础理论、基本知识和方法技能，详尽地反映了商业银行基本业务的会计处理。本教材的编写立足于应用型人才培养，吸收了多所应用型本科院校一些成熟教材的精华，融入了编者在实务及教学工作中的经验和体会，着力培养学生实践能力和创新能力。本教材以教学和实际工作需要为目标，按照教育部公布的教学基本要求，以简明、精练的语言阐述知识要点，深入浅出、通俗易懂，理论联系实际，实务操作规范，符合市场经济条件下的应用型人才培养的要求。本教材除正文外，每章还设计了本章导言、本章知识结构图、 本章小结、复习思考题、延伸阅读等环节，使其在形式上更加新颖，在结构上更加完整，从而更有利于学生自主学习。<br>','本教材系统地介绍了我国商业银行会计的基础理论、基本知识和方法技能，详尽地反映了商业银行基本业务的会计处理。本教材的编写立足于应用型人才培养，吸收了多所应用型本科院校一些成熟教材的精华，融入了编者在实务及教学工作中的经验和体会，着力培养学生实践能力和创新能力。本教材以教学和实际工作需要为目标，按照教育部公布的教学基本要求，以简明、精练的语言阐述知识要点，深入浅出、通俗易懂，理论联系实际，实务操作规范，符合市场经济条件下的应用型人才培养的要求。本教材除正文外，每章还设计了本章导言、本章知识结构图、 本章小结、复习思考题、延伸阅读等环节，使其在形式上更加新颖，在结构上更加完整，从而更有利于学生自主学习。<br>',NULL,NULL,NULL,'2015-06-30 13:35:57','Admin','4732b2ca-c53d-4d2a-bdaf-51343212f5f2.jpg','1b90ce64-e848-41cc-b708-515b4ea4cd9d.jpg','92c4c440-7a7b-4c4e-a76f-b22fdff6c4ef.jpg','a3b38b62-daee-4c98-b5d9-52edfb727039.jpg',NULL,NULL,0,NULL,21,0,NULL,'swny'),(22,'数学类','物理类','国际金融','贺瑛','高等教育出版社','23423489',1,'平装',16,'2012-12-12',122,12.1,'<p>《国际金融/高等学校应用创新型人才培训系列教材》注重国际金融理论的应用性和实用性，在保证国际金融基本理论完整的前提下，侧重于国际金融实务的操作。在分析国际金融理论和知识时，注重结合中国的对外货币金融问题进行说明。同时注意吸取目前较先进的教学经验，以及本学科应用领域的最新研究成果和知识。全书共分10章，主要阐述国际金融概述、国际收支、外汇与汇率、外汇市场与外汇交易、汇率制度、国际储备、国际金融市场、国际货币体系、国际资本流动、国际金融机构等内容。《国际金融/高等学校应用创新型人才培训系列教材》采用国际流行的教材编写体例，每章前都有本章导言，章后有本章小结、延伸阅读、本章案例和复习思考题，篇中提供了与教材知识点相配套的拓展阅读内容。</p><p>本书适合普通高校作为经济管理类专业的专业基础课教材，也可作为金融、经济从业人员的培训教材，还可以作为广大社会工作者了解国际金融知识的阅读书籍。<br></p>','<p>《国际金融/高等学校应用创新型人才培训系列教材》注重国际金融理论的应用性和实用性，在保证国际金融基本理论完整的前提下，侧重于国际金融实务的操作。在分析国际金融理论和知识时，注重结合中国的对外货币金融问题进行说明。同时注意吸取目前较先进的教学经验，以及本学科应用领域的最新研究成果和知识。全书共分10章，主要阐述国际金融概述、国际收支、外汇与汇率、外汇市场与外汇交易、汇率制度、国际储备、国际金融市场、国际货币体系、国际资本流动、国际金融机构等内容。《国际金融/高等学校应用创新型人才培训系列教材》采用国际流行的教材编写体例，每章前都有本章导言，章后有本章小结、延伸阅读、本章案例和复习思考题，篇中提供了与教材知识点相配套的拓展阅读内容。</p><p>本书适合普通高校作为经济管理类专业的专业基础课教材，也可作为金融、经济从业人员的培训教材，还可以作为广大社会工作者了解国际金融知识的阅读书籍。<br></p>',NULL,NULL,NULL,'2015-06-30 14:05:09','Admin','0a1355bf-b33d-41e9-9ca3-16b59c427482.jpg','8315288c-819c-45fa-8081-37ad42b97ecf.jpg','a58cf1d5-1b5a-48f3-aefd-dbc62fa51335.jpg','48973f26-f5cf-471c-a4d1-e44717799744.jpg',NULL,NULL,0,NULL,22,0,NULL,'swny'),(23,'数学类','无','国际金融实务','黄梅波 熊爱宗','高等教育出版社','1232323989',4,'平装',16,'2014-03-15',348,123.2,'本书是高等学校国际经济与贸易专业主要课程教材，它对各种金融工具及其实务操作进行了详细的介绍。全书共四篇十五章。第一篇介绍了主要的外汇业务与金融衍生交易，包括外汇与外汇业务的基本概念和各种衍生工具及其操作（如期货和期货交易、期权与期权交易、互换与远期利率协议等）。第二篇对国际金融风险管理进行了探讨，包括商业银行的风险管理、工商企业的风险管理以及国家风险评估与管理等。第三篇探讨了国际融资业务，主要介绍了如何利用国际金融市场进行信贷融资、债券融资和股票融资。第四篇对跨国投资业务进行了介绍，包括跨国投资的资本成本和资本结构、国际资产组合投资以及跨国公司资本预算管理等。<p>本书可作为高等院校经济类专业本科或研究生的教学用书，也可以作为从事实际经济工作人员的参考用书。<br></p>','本书是高等学校国际经济与贸易专业主要课程教材，它对各种金融工具及其实务操作进行了详细的介绍。全书共四篇十五章。第一篇介绍了主要的外汇业务与金融衍生交易，包括外汇与外汇业务的基本概念和各种衍生工具及其操作（如期货和期货交易、期权与期权交易、互换与远期利率协议等）。第二篇对国际金融风险管理进行了探讨，包括商业银行的风险管理、工商企业的风险管理以及国家风险评估与管理等。第三篇探讨了国际融资业务，主要介绍了如何利用国际金融市场进行信贷融资、债券融资和股票融资。第四篇对跨国投资业务进行了介绍，包括跨国投资的资本成本和资本结构、国际资产组合投资以及跨国公司资本预算管理等。<p>本书可作为高等院校经济类专业本科或研究生的教学用书，也可以作为从事实际经济工作人员的参考用书。<br></p>',NULL,NULL,NULL,'2015-06-30 14:06:50','Admin','e49b98d3-bd71-4d7b-a13f-313726c9c9a5.jpg','24f9047c-452e-4120-bfac-2c7a6b99d098.jpg','82d0335a-f2e9-476b-950f-64b7bfad0eac.jpg','737c038a-1e5a-4e92-b2bd-aa1f6273f7da.jpg',NULL,NULL,0,NULL,24,0,NULL,'swny'),(24,'数学类','无','银行授信实务','陆明祥 杨则文','高等教育出版社','3123123123123',1,'平装',16,'2012-08--9',547,78.9,'<p>高等职业教育金融专业教学资源库项目（项目编号：2012-2-6）是教育部、财政部为深化高职教育教学改革，加强专业与课程建设，推动优质教学资源共建共享，提高人才培养质量而启动的国家级高职教育建设项目。本书是高等职业教育专业教学资源库建设项目规划教材，并已入选“十二五”职业教育国家规划教材立项选题。</p><p>本书是教育部“高等职业教育金融专业教学资源库建设项目”的建设成果之一。本书以金融企业的服务需求为主线，以金融服务礼仪的工作任务与要求为依据，强调内容的应用性和操作性，力争突出学生职业素质养成的主体性。本书共有九章：第一章，金融行业工作人员礼仪修养的基础理论；第二章，金融行业工作人员的仪表礼仪；第三章，金融行业工作人员的仪态礼仪；第四章，金融行业工作人员的语言礼仪；第五章，金融行业工作人员的日常交际礼仪；第六章，金融行业公务礼仪；第七章，金融行业岗位服务礼仪；第八章，金融行业营销礼仪；第九章，金融行业涉外服务礼仪。正文设有引例、同步案例、同步思考、同步训练、小常识、活动设计等内容，在结束时都提供了专业能力训练和专业能力考核的内容，便于学生自查和教师考评。</p><p>本书既可作为高等职业技术学院对学生进行素质教育和开放礼仪课的教材，也可作为金融行业员工了解和掌握金融职业礼仪知识、提高素质、加强修养的工具书。<br></p>','<p>高等职业教育金融专业教学资源库项目（项目编号：2012-2-6）是教育部、财政部为深化高职教育教学改革，加强专业与课程建设，推动优质教学资源共建共享，提高人才培养质量而启动的国家级高职教育建设项目。本书是高等职业教育专业教学资源库建设项目规划教材，并已入选“十二五”职业教育国家规划教材立项选题。</p><p>本书是教育部“高等职业教育金融专业教学资源库建设项目”的建设成果之一。本书以金融企业的服务需求为主线，以金融服务礼仪的工作任务与要求为依据，强调内容的应用性和操作性，力争突出学生职业素质养成的主体性。本书共有九章：第一章，金融行业工作人员礼仪修养的基础理论；第二章，金融行业工作人员的仪表礼仪；第三章，金融行业工作人员的仪态礼仪；第四章，金融行业工作人员的语言礼仪；第五章，金融行业工作人员的日常交际礼仪；第六章，金融行业公务礼仪；第七章，金融行业岗位服务礼仪；第八章，金融行业营销礼仪；第九章，金融行业涉外服务礼仪。正文设有引例、同步案例、同步思考、同步训练、小常识、活动设计等内容，在结束时都提供了专业能力训练和专业能力考核的内容，便于学生自查和教师考评。</p><p>本书既可作为高等职业技术学院对学生进行素质教育和开放礼仪课的教材，也可作为金融行业员工了解和掌握金融职业礼仪知识、提高素质、加强修养的工具书。<br></p>',NULL,NULL,NULL,'2015-06-30 14:08:58','Admin','05b4ea9e-29cc-4202-bef6-9ccfd5e2630f.jpg','8b19b1a4-486d-4cde-a153-114d52978175.jpg','0478c143-361a-4f80-9d58-b32b7273483b.jpg','a82fa9f0-9095-4b6c-a9d5-acc39e93398c.jpg',NULL,NULL,0,NULL,23,0,NULL,'swny'),(25,'数学类','物理类','会计学原理','袁伟','高等教育出版社','123213',1,'平装',16,'2012-12-31',678,123.2,'本教材系统地介绍了我国商业银行会计的基础理论、基本知识和方法技能，详尽地反映了商业银行基本业务的会计处理。本教材的编写立足于应用型人才培养，吸收了多所应用型本科院校一些成熟教材的精华，融入了编者在实务及教学工作中的经验和体会，着力培养学生实践能力和创新能力。本教材以教学和实际工作需要为目标，按照教育部公布的教学基本要求，以简明、精练的语言阐述知识要点，深入浅出、通俗易懂，理论联系实际，实务操作规范，符合市场经济条件下的应用型人才培养的要求。本教材除正文外，每章还设计了本章导言、本章知识结构图、 本章小结、复习思考题、延伸阅读等环节，使其在形式上更加新颖，在结构上更加完整，从而更有利于学生自主学习。<br>','本教材系统地介绍了我国商业银行会计的基础理论、基本知识和方法技能，详尽地反映了商业银行基本业务的会计处理。本教材的编写立足于应用型人才培养，吸收了多所应用型本科院校一些成熟教材的精华，融入了编者在实务及教学工作中的经验和体会，着力培养学生实践能力和创新能力。本教材以教学和实际工作需要为目标，按照教育部公布的教学基本要求，以简明、精练的语言阐述知识要点，深入浅出、通俗易懂，理论联系实际，实务操作规范，符合市场经济条件下的应用型人才培养的要求。本教材除正文外，每章还设计了本章导言、本章知识结构图、 本章小结、复习思考题、延伸阅读等环节，使其在形式上更加新颖，在结构上更加完整，从而更有利于学生自主学习。<br>',NULL,NULL,NULL,'2015-06-30 13:35:57','Admin','4732b2ca-c53d-4d2a-bdaf-51343212f5f2.jpg','1b90ce64-e848-41cc-b708-515b4ea4cd9d.jpg','92c4c440-7a7b-4c4e-a76f-b22fdff6c4ef.jpg','a3b38b62-daee-4c98-b5d9-52edfb727039.jpg',NULL,NULL,0,NULL,27,0,NULL,'swny'),(26,'数学类','物理类','国际金融','贺瑛','高等教育出版社','23423489',1,'平装',16,'2012-12-12',122,12.1,'<p>《国际金融/高等学校应用创新型人才培训系列教材》注重国际金融理论的应用性和实用性，在保证国际金融基本理论完整的前提下，侧重于国际金融实务的操作。在分析国际金融理论和知识时，注重结合中国的对外货币金融问题进行说明。同时注意吸取目前较先进的教学经验，以及本学科应用领域的最新研究成果和知识。全书共分10章，主要阐述国际金融概述、国际收支、外汇与汇率、外汇市场与外汇交易、汇率制度、国际储备、国际金融市场、国际货币体系、国际资本流动、国际金融机构等内容。《国际金融/高等学校应用创新型人才培训系列教材》采用国际流行的教材编写体例，每章前都有本章导言，章后有本章小结、延伸阅读、本章案例和复习思考题，篇中提供了与教材知识点相配套的拓展阅读内容。</p><p>本书适合普通高校作为经济管理类专业的专业基础课教材，也可作为金融、经济从业人员的培训教材，还可以作为广大社会工作者了解国际金融知识的阅读书籍。<br></p>','<p>《国际金融/高等学校应用创新型人才培训系列教材》注重国际金融理论的应用性和实用性，在保证国际金融基本理论完整的前提下，侧重于国际金融实务的操作。在分析国际金融理论和知识时，注重结合中国的对外货币金融问题进行说明。同时注意吸取目前较先进的教学经验，以及本学科应用领域的最新研究成果和知识。全书共分10章，主要阐述国际金融概述、国际收支、外汇与汇率、外汇市场与外汇交易、汇率制度、国际储备、国际金融市场、国际货币体系、国际资本流动、国际金融机构等内容。《国际金融/高等学校应用创新型人才培训系列教材》采用国际流行的教材编写体例，每章前都有本章导言，章后有本章小结、延伸阅读、本章案例和复习思考题，篇中提供了与教材知识点相配套的拓展阅读内容。</p><p>本书适合普通高校作为经济管理类专业的专业基础课教材，也可作为金融、经济从业人员的培训教材，还可以作为广大社会工作者了解国际金融知识的阅读书籍。<br></p>',NULL,NULL,NULL,'2015-06-30 14:05:09','Admin','0a1355bf-b33d-41e9-9ca3-16b59c427482.jpg','8315288c-819c-45fa-8081-37ad42b97ecf.jpg','a58cf1d5-1b5a-48f3-aefd-dbc62fa51335.jpg','48973f26-f5cf-471c-a4d1-e44717799744.jpg',NULL,NULL,0,NULL,25,0,NULL,'swny'),(27,'数学类','无','国际金融实务','黄梅波 熊爱宗','高等教育出版社','1232323989',4,'平装',16,'2014-03-15',348,123.2,'本书是高等学校国际经济与贸易专业主要课程教材，它对各种金融工具及其实务操作进行了详细的介绍。全书共四篇十五章。第一篇介绍了主要的外汇业务与金融衍生交易，包括外汇与外汇业务的基本概念和各种衍生工具及其操作（如期货和期货交易、期权与期权交易、互换与远期利率协议等）。第二篇对国际金融风险管理进行了探讨，包括商业银行的风险管理、工商企业的风险管理以及国家风险评估与管理等。第三篇探讨了国际融资业务，主要介绍了如何利用国际金融市场进行信贷融资、债券融资和股票融资。第四篇对跨国投资业务进行了介绍，包括跨国投资的资本成本和资本结构、国际资产组合投资以及跨国公司资本预算管理等。<p>本书可作为高等院校经济类专业本科或研究生的教学用书，也可以作为从事实际经济工作人员的参考用书。<br></p>','本书是高等学校国际经济与贸易专业主要课程教材，它对各种金融工具及其实务操作进行了详细的介绍。全书共四篇十五章。第一篇介绍了主要的外汇业务与金融衍生交易，包括外汇与外汇业务的基本概念和各种衍生工具及其操作（如期货和期货交易、期权与期权交易、互换与远期利率协议等）。第二篇对国际金融风险管理进行了探讨，包括商业银行的风险管理、工商企业的风险管理以及国家风险评估与管理等。第三篇探讨了国际融资业务，主要介绍了如何利用国际金融市场进行信贷融资、债券融资和股票融资。第四篇对跨国投资业务进行了介绍，包括跨国投资的资本成本和资本结构、国际资产组合投资以及跨国公司资本预算管理等。<p>本书可作为高等院校经济类专业本科或研究生的教学用书，也可以作为从事实际经济工作人员的参考用书。<br></p>',NULL,NULL,NULL,'2015-06-30 14:06:50','Admin','e49b98d3-bd71-4d7b-a13f-313726c9c9a5.jpg','24f9047c-452e-4120-bfac-2c7a6b99d098.jpg','82d0335a-f2e9-476b-950f-64b7bfad0eac.jpg','737c038a-1e5a-4e92-b2bd-aa1f6273f7da.jpg',NULL,NULL,0,NULL,29,0,NULL,'swny'),(28,'数学类','无','银行授信实务','陆明祥 杨则文','高等教育出版社','3123123123123',1,'平装',16,'2012-08--9',547,78.9,'<p>高等职业教育金融专业教学资源库项目（项目编号：2012-2-6）是教育部、财政部为深化高职教育教学改革，加强专业与课程建设，推动优质教学资源共建共享，提高人才培养质量而启动的国家级高职教育建设项目。本书是高等职业教育专业教学资源库建设项目规划教材，并已入选“十二五”职业教育国家规划教材立项选题。</p><p>本书是教育部“高等职业教育金融专业教学资源库建设项目”的建设成果之一。本书以金融企业的服务需求为主线，以金融服务礼仪的工作任务与要求为依据，强调内容的应用性和操作性，力争突出学生职业素质养成的主体性。本书共有九章：第一章，金融行业工作人员礼仪修养的基础理论；第二章，金融行业工作人员的仪表礼仪；第三章，金融行业工作人员的仪态礼仪；第四章，金融行业工作人员的语言礼仪；第五章，金融行业工作人员的日常交际礼仪；第六章，金融行业公务礼仪；第七章，金融行业岗位服务礼仪；第八章，金融行业营销礼仪；第九章，金融行业涉外服务礼仪。正文设有引例、同步案例、同步思考、同步训练、小常识、活动设计等内容，在结束时都提供了专业能力训练和专业能力考核的内容，便于学生自查和教师考评。</p><p>本书既可作为高等职业技术学院对学生进行素质教育和开放礼仪课的教材，也可作为金融行业员工了解和掌握金融职业礼仪知识、提高素质、加强修养的工具书。<br></p>','<p>高等职业教育金融专业教学资源库项目（项目编号：2012-2-6）是教育部、财政部为深化高职教育教学改革，加强专业与课程建设，推动优质教学资源共建共享，提高人才培养质量而启动的国家级高职教育建设项目。本书是高等职业教育专业教学资源库建设项目规划教材，并已入选“十二五”职业教育国家规划教材立项选题。</p><p>本书是教育部“高等职业教育金融专业教学资源库建设项目”的建设成果之一。本书以金融企业的服务需求为主线，以金融服务礼仪的工作任务与要求为依据，强调内容的应用性和操作性，力争突出学生职业素质养成的主体性。本书共有九章：第一章，金融行业工作人员礼仪修养的基础理论；第二章，金融行业工作人员的仪表礼仪；第三章，金融行业工作人员的仪态礼仪；第四章，金融行业工作人员的语言礼仪；第五章，金融行业工作人员的日常交际礼仪；第六章，金融行业公务礼仪；第七章，金融行业岗位服务礼仪；第八章，金融行业营销礼仪；第九章，金融行业涉外服务礼仪。正文设有引例、同步案例、同步思考、同步训练、小常识、活动设计等内容，在结束时都提供了专业能力训练和专业能力考核的内容，便于学生自查和教师考评。</p><p>本书既可作为高等职业技术学院对学生进行素质教育和开放礼仪课的教材，也可作为金融行业员工了解和掌握金融职业礼仪知识、提高素质、加强修养的工具书。<br></p>',NULL,NULL,NULL,'2015-06-30 14:08:58','Admin','05b4ea9e-29cc-4202-bef6-9ccfd5e2630f.jpg','8b19b1a4-486d-4cde-a153-114d52978175.jpg','0478c143-361a-4f80-9d58-b32b7273483b.jpg','a82fa9f0-9095-4b6c-a9d5-acc39e93398c.jpg',NULL,NULL,0,NULL,28,0,NULL,'swny'),(29,'数学类','物理类','会计学原理','袁伟','高等教育出版社','123213',1,'平装',16,'2012-12-31',678,123.2,'本教材系统地介绍了我国商业银行会计的基础理论、基本知识和方法技能，详尽地反映了商业银行基本业务的会计处理。本教材的编写立足于应用型人才培养，吸收了多所应用型本科院校一些成熟教材的精华，融入了编者在实务及教学工作中的经验和体会，着力培养学生实践能力和创新能力。本教材以教学和实际工作需要为目标，按照教育部公布的教学基本要求，以简明、精练的语言阐述知识要点，深入浅出、通俗易懂，理论联系实际，实务操作规范，符合市场经济条件下的应用型人才培养的要求。本教材除正文外，每章还设计了本章导言、本章知识结构图、 本章小结、复习思考题、延伸阅读等环节，使其在形式上更加新颖，在结构上更加完整，从而更有利于学生自主学习。<br>','本教材系统地介绍了我国商业银行会计的基础理论、基本知识和方法技能，详尽地反映了商业银行基本业务的会计处理。本教材的编写立足于应用型人才培养，吸收了多所应用型本科院校一些成熟教材的精华，融入了编者在实务及教学工作中的经验和体会，着力培养学生实践能力和创新能力。本教材以教学和实际工作需要为目标，按照教育部公布的教学基本要求，以简明、精练的语言阐述知识要点，深入浅出、通俗易懂，理论联系实际，实务操作规范，符合市场经济条件下的应用型人才培养的要求。本教材除正文外，每章还设计了本章导言、本章知识结构图、 本章小结、复习思考题、延伸阅读等环节，使其在形式上更加新颖，在结构上更加完整，从而更有利于学生自主学习。<br>',NULL,NULL,NULL,'2015-06-30 13:35:57','Admin','4732b2ca-c53d-4d2a-bdaf-51343212f5f2.jpg','1b90ce64-e848-41cc-b708-515b4ea4cd9d.jpg','92c4c440-7a7b-4c4e-a76f-b22fdff6c4ef.jpg','a3b38b62-daee-4c98-b5d9-52edfb727039.jpg',NULL,NULL,0,NULL,26,0,NULL,'swny'),(30,'数学类','物理类','国际金融','贺瑛','高等教育出版社','23423489',1,'平装',16,'2012-12-12',122,12.1,'<p>《国际金融/高等学校应用创新型人才培训系列教材》注重国际金融理论的应用性和实用性，在保证国际金融基本理论完整的前提下，侧重于国际金融实务的操作。在分析国际金融理论和知识时，注重结合中国的对外货币金融问题进行说明。同时注意吸取目前较先进的教学经验，以及本学科应用领域的最新研究成果和知识。全书共分10章，主要阐述国际金融概述、国际收支、外汇与汇率、外汇市场与外汇交易、汇率制度、国际储备、国际金融市场、国际货币体系、国际资本流动、国际金融机构等内容。《国际金融/高等学校应用创新型人才培训系列教材》采用国际流行的教材编写体例，每章前都有本章导言，章后有本章小结、延伸阅读、本章案例和复习思考题，篇中提供了与教材知识点相配套的拓展阅读内容。</p><p>本书适合普通高校作为经济管理类专业的专业基础课教材，也可作为金融、经济从业人员的培训教材，还可以作为广大社会工作者了解国际金融知识的阅读书籍。<br></p>','<p>《国际金融/高等学校应用创新型人才培训系列教材》注重国际金融理论的应用性和实用性，在保证国际金融基本理论完整的前提下，侧重于国际金融实务的操作。在分析国际金融理论和知识时，注重结合中国的对外货币金融问题进行说明。同时注意吸取目前较先进的教学经验，以及本学科应用领域的最新研究成果和知识。全书共分10章，主要阐述国际金融概述、国际收支、外汇与汇率、外汇市场与外汇交易、汇率制度、国际储备、国际金融市场、国际货币体系、国际资本流动、国际金融机构等内容。《国际金融/高等学校应用创新型人才培训系列教材》采用国际流行的教材编写体例，每章前都有本章导言，章后有本章小结、延伸阅读、本章案例和复习思考题，篇中提供了与教材知识点相配套的拓展阅读内容。</p><p>本书适合普通高校作为经济管理类专业的专业基础课教材，也可作为金融、经济从业人员的培训教材，还可以作为广大社会工作者了解国际金融知识的阅读书籍。<br></p>',NULL,NULL,NULL,'2015-06-30 14:05:09','Admin','0a1355bf-b33d-41e9-9ca3-16b59c427482.jpg','8315288c-819c-45fa-8081-37ad42b97ecf.jpg','a58cf1d5-1b5a-48f3-aefd-dbc62fa51335.jpg','48973f26-f5cf-471c-a4d1-e44717799744.jpg',NULL,NULL,0,NULL,31,0,NULL,'swny'),(31,'数学类','无','国际金融实务','黄梅波 熊爱宗','高等教育出版社','1232323989',4,'平装',16,'2014-03-15',348,123.2,'本书是高等学校国际经济与贸易专业主要课程教材，它对各种金融工具及其实务操作进行了详细的介绍。全书共四篇十五章。第一篇介绍了主要的外汇业务与金融衍生交易，包括外汇与外汇业务的基本概念和各种衍生工具及其操作（如期货和期货交易、期权与期权交易、互换与远期利率协议等）。第二篇对国际金融风险管理进行了探讨，包括商业银行的风险管理、工商企业的风险管理以及国家风险评估与管理等。第三篇探讨了国际融资业务，主要介绍了如何利用国际金融市场进行信贷融资、债券融资和股票融资。第四篇对跨国投资业务进行了介绍，包括跨国投资的资本成本和资本结构、国际资产组合投资以及跨国公司资本预算管理等。<p>本书可作为高等院校经济类专业本科或研究生的教学用书，也可以作为从事实际经济工作人员的参考用书。<br></p>','本书是高等学校国际经济与贸易专业主要课程教材，它对各种金融工具及其实务操作进行了详细的介绍。全书共四篇十五章。第一篇介绍了主要的外汇业务与金融衍生交易，包括外汇与外汇业务的基本概念和各种衍生工具及其操作（如期货和期货交易、期权与期权交易、互换与远期利率协议等）。第二篇对国际金融风险管理进行了探讨，包括商业银行的风险管理、工商企业的风险管理以及国家风险评估与管理等。第三篇探讨了国际融资业务，主要介绍了如何利用国际金融市场进行信贷融资、债券融资和股票融资。第四篇对跨国投资业务进行了介绍，包括跨国投资的资本成本和资本结构、国际资产组合投资以及跨国公司资本预算管理等。<p>本书可作为高等院校经济类专业本科或研究生的教学用书，也可以作为从事实际经济工作人员的参考用书。<br></p>',NULL,NULL,NULL,'2015-06-30 14:06:50','Admin','e49b98d3-bd71-4d7b-a13f-313726c9c9a5.jpg','24f9047c-452e-4120-bfac-2c7a6b99d098.jpg','82d0335a-f2e9-476b-950f-64b7bfad0eac.jpg','737c038a-1e5a-4e92-b2bd-aa1f6273f7da.jpg',NULL,NULL,0,NULL,32,0,NULL,'swny'),(32,'数学类','无','银行授信实务','陆明祥 杨则文','高等教育出版社','3123123123123',1,'平装',16,'2012-08--9',547,78.9,'<p>高等职业教育金融专业教学资源库项目（项目编号：2012-2-6）是教育部、财政部为深化高职教育教学改革，加强专业与课程建设，推动优质教学资源共建共享，提高人才培养质量而启动的国家级高职教育建设项目。本书是高等职业教育专业教学资源库建设项目规划教材，并已入选“十二五”职业教育国家规划教材立项选题。</p><p>本书是教育部“高等职业教育金融专业教学资源库建设项目”的建设成果之一。本书以金融企业的服务需求为主线，以金融服务礼仪的工作任务与要求为依据，强调内容的应用性和操作性，力争突出学生职业素质养成的主体性。本书共有九章：第一章，金融行业工作人员礼仪修养的基础理论；第二章，金融行业工作人员的仪表礼仪；第三章，金融行业工作人员的仪态礼仪；第四章，金融行业工作人员的语言礼仪；第五章，金融行业工作人员的日常交际礼仪；第六章，金融行业公务礼仪；第七章，金融行业岗位服务礼仪；第八章，金融行业营销礼仪；第九章，金融行业涉外服务礼仪。正文设有引例、同步案例、同步思考、同步训练、小常识、活动设计等内容，在结束时都提供了专业能力训练和专业能力考核的内容，便于学生自查和教师考评。</p><p>本书既可作为高等职业技术学院对学生进行素质教育和开放礼仪课的教材，也可作为金融行业员工了解和掌握金融职业礼仪知识、提高素质、加强修养的工具书。<br></p>','<p>高等职业教育金融专业教学资源库项目（项目编号：2012-2-6）是教育部、财政部为深化高职教育教学改革，加强专业与课程建设，推动优质教学资源共建共享，提高人才培养质量而启动的国家级高职教育建设项目。本书是高等职业教育专业教学资源库建设项目规划教材，并已入选“十二五”职业教育国家规划教材立项选题。</p><p>本书是教育部“高等职业教育金融专业教学资源库建设项目”的建设成果之一。本书以金融企业的服务需求为主线，以金融服务礼仪的工作任务与要求为依据，强调内容的应用性和操作性，力争突出学生职业素质养成的主体性。本书共有九章：第一章，金融行业工作人员礼仪修养的基础理论；第二章，金融行业工作人员的仪表礼仪；第三章，金融行业工作人员的仪态礼仪；第四章，金融行业工作人员的语言礼仪；第五章，金融行业工作人员的日常交际礼仪；第六章，金融行业公务礼仪；第七章，金融行业岗位服务礼仪；第八章，金融行业营销礼仪；第九章，金融行业涉外服务礼仪。正文设有引例、同步案例、同步思考、同步训练、小常识、活动设计等内容，在结束时都提供了专业能力训练和专业能力考核的内容，便于学生自查和教师考评。</p><p>本书既可作为高等职业技术学院对学生进行素质教育和开放礼仪课的教材，也可作为金融行业员工了解和掌握金融职业礼仪知识、提高素质、加强修养的工具书。<br></p>',NULL,NULL,NULL,'2015-06-30 14:08:58','Admin','05b4ea9e-29cc-4202-bef6-9ccfd5e2630f.jpg','8b19b1a4-486d-4cde-a153-114d52978175.jpg','0478c143-361a-4f80-9d58-b32b7273483b.jpg','a82fa9f0-9095-4b6c-a9d5-acc39e93398c.jpg',NULL,NULL,0,NULL,30,0,NULL,'swny');

