/*Table structure for table `zzjy` */

DROP TABLE IF EXISTS `zzjy`;

CREATE TABLE `zzjy` (
  `id` int(12) NOT NULL AUTO_INCREMENT,
  `fenlei` varchar(155) DEFAULT NULL COMMENT '高等文科下的分类',
  `fenlei2` varchar(155) DEFAULT NULL,
  `shuming` varchar(255) NOT NULL COMMENT '图书名',
  `zuozhe` varchar(255) NOT NULL COMMENT '作者',
  `chubanshe` varchar(255) NOT NULL COMMENT '出版社',
  `isbn` varchar(155) NOT NULL COMMENT 'ISBN',
  `banci` int(2) DEFAULT '1' COMMENT '版次',
  `baozhuang` varchar(155) DEFAULT NULL COMMENT '简装/平装',
  `kaiben` int(2) DEFAULT NULL COMMENT '开本',
  `chubanshijian` varchar(155) DEFAULT NULL COMMENT '出版时间',
  `yeshu` int(5) DEFAULT NULL COMMENT '页数',
  `dingjia` float DEFAULT NULL,
  `neirongjianjie` mediumtext,
  `zuozhejianjie` mediumtext,
  `mulu` mediumtext,
  `jingcaishuzhai` mediumtext,
  `qianyan` mediumtext,
  `tianjiashijian` varchar(155) DEFAULT NULL,
  `tianjiaren` varchar(155) DEFAULT NULL,
  `tupian1` varchar(155) DEFAULT NULL,
  `tupian2` varchar(155) DEFAULT NULL,
  `tupian3` varchar(155) DEFAULT NULL,
  `tupian4` varchar(155) DEFAULT NULL,
  `gengxinren` varchar(155) DEFAULT NULL,
  `gengxinshijian` varchar(155) DEFAULT NULL,
  `zhanshi` int(1) DEFAULT '0' COMMENT '1.首页展示 0.不展示',
  `zhanshishijian` timestamp NULL DEFAULT NULL COMMENT '设置展示时间',
  `paixu` int(12) DEFAULT NULL COMMENT '排序',
  `zhiding` int(1) DEFAULT '0' COMMENT '1.置顶 0.不置顶',
  `zhidingshijian` timestamp NULL DEFAULT NULL,
  `type` varchar(55) DEFAULT 'zzjy',
  PRIMARY KEY (`id`),
  KEY `isbn` (`isbn`),
